from fact_fibo.factorial import factorial_recursive
from fact_fibo.factorial import factorial
import unittest
class Test_factorial(unittest.TestCase):

   def test_fact_norm(self):
      self.assertEqual(factorial(10), 3628800)


   def test_fact_zero(self):
      self.assertEqual(factorial(0), 1)


   def test_fact_big(self):
      self.assertEqual(factorial(1000), )


   def test_fact_very_big(self):
      with self.assertRaises(ValueError):
         factorial(6000000)


   def test_fact_negative(self):
      with self.assertRaises(ValueError):
         factorial(-5000)


class Test_factorial_recursive(unittest.TestCase):

   def test_fact_recurs_norm(self):
      self.assertEqual(factorial_recursive(10), 3628800)


   def test_fact_zero(self):
      self.assertEqual(factorial_recursive(0), 1)


   def test_fact_recurs_big(self):
      self.assertEqual(factorial_recursive(900), )


   def test_fact_recurs_very_big(self):
      with self.assertRaises(ValueError):
         factorial_recursive(9600)


   def test_fact_recurs_negative(self):
      with self.assertRaises(ValueError):
         factorial_recursive(-43000)